import "./index.css";

import { Component } from "react";

import NoteList from "../NoteList";

class Notes extends Component {
  state = { notesData: [], title: "", notes: "" };

  onAddNote = (event) => {};

  render() {
    const { notesData } = this.state;
    // const notesCount = notesData.count;
    return (
      <div className="bg-container ">
        <h1 className="main-head">Notes</h1>
        <div className="notes-container">
          <form className="remainder-input-container" onSubmit={this.onAddNote}>
            <h1 className="sub-text">New Notes</h1>
            <label for="noteTitle" className="remainder-input-label">
              Title
            </label>
            <input
              className="notes-title-input"
              type="text"
              id="noteTitle"
              placeholder="Title"
            />
            <textarea
              rows="20"
              cols="50"
              id="text-area"
              className="text-area-input"
              placeholder="Write your Notes Here"
            />
            <div className="notes-button-container">
              <button
                className="button1 clear-button"
                type="submit"
                name="clearButton"
              >
                Clear
              </button>
              <button className="button1" type="submit" name="addButton">
                Add Notes
              </button>
            </div>
          </form>
          <div className="notes-list-container">
            <ul>
              {notesData.map((eachNote) => (
                <NoteList noteDetail={eachNote} />
              ))}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Notes;
